import React, { Component } from 'react';
import './App.css'; // Menghubungkan file CSS

// Komponen Anak untuk menampilkan data dalam tabel
class TabelData extends Component {
  render() {
    const data = this.props.data.map((item, index) => (
      <tr key={index}>
        <td>{item.nama}</td>
        <td>{item.umur}</td>
      </tr>
    ));

    return (
      <table className="table">
        <thead>
          <tr>
            <th>Nama</th>
            <th>Umur</th>
          </tr>
        </thead>
        <tbody>
          {data}
        </tbody>
      </table>
    );
  }
}

// Komponen Induk yang menyimpan state dan menyediakan gambar
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [
        { nama: 'John', umur: 25 },
        { nama: 'Doe', umur: 30 },
        { nama: 'Jane', umur: 28 }
      ],
      imageUrl: 'https://via.placeholder.com/150' // URL gambar placeholder
    };
    this.changeImage = this.changeImage.bind(this);
  }

  changeImage() {
    // Mengubah gambar ketika diklik
    this.setState({
      imageUrl: 'https://via.placeholder.com/300' // Mengubah URL gambar
    });
  }

  render() {
    return (
      <div className="container">
        <h1>Data Pengguna</h1>
        <TabelData data={this.state.data} />
        <div className="image-container" onClick={this.changeImage}>
          <img src={this.state.imageUrl} alt="Placeholder" />
        </div>
      </div>
    );
  }
}

export default App;
