import React from "react";

class Eventreact extends React.Component{
        render() {
          const { data } = this.props;
          const rows = data.map((item, index) => (
            <tr key={index}>
              <td>{item.nama}</td>
              <td>{item.umur}</td>
            </tr>
          ));
      
          return (
            <table className="table">
              <thead>
                <tr>
                  <th>Nama</th>
                  <th>Umur</th>
                </tr>
              </thead>
              <tbody>
                {rows}
              </tbody>
            </table>
          );
        }
      }
      
      // Komponen Induk yang menyimpan state dan menyediakan gambar
      class App extends Component {
        constructor(props) {
          super(props);
          this.state = {
            data: this.props.defaultData,
            namaBaru: '',
            umurBaru: ''
          };
          this.handleNamaChange = this.handleNamaChange.bind(this);
          this.handleUmurChange = this.handleUmurChange.bind(this);
          this.handleSubmit = this.handleSubmit.bind(this);
          this.handleHapusSemua = this.handleHapusSemua.bind(this);
        }
      
        handleNamaChange(event) {
          this.setState({ namaBaru: event.target.value });
        }
      
        handleUmurChange(event) {
          this.setState({ umurBaru: event.target.value });
        }
      
        handleSubmit(event) {
          event.preventDefault();
          const { namaBaru, umurBaru } = this.state;
          if (namaBaru.trim() !== '' && !isNaN(parseInt(umurBaru))) {
            const newData = {
              nama: namaBaru,
              umur: parseInt(umurBaru)
            };
            this.setState(prevState => ({
              data: [...prevState.data, newData],
              namaBaru: '',
              umurBaru: ''
            }));
          } else {
            alert("Input tidak valid. Pastikan Anda memasukkan nama dan umur yang valid.");
          }
        }
      
        handleHapusSemua() {
          this.setState({ data: [] });
        }
      
        render() {
          return (
            <div className="container">
              <h1>Data Pengguna</h1>
              <form onSubmit={this.handleSubmit}>
                <label>
                  Nama:
                  <input type="text" value={this.state.namaBaru} onChange={this.handleNamaChange} />
                </label>
                <label>
                  Umur:
                  <input type="text" value={this.state.umurBaru} onChange={this.handleUmurChange} />
                </label>
                <button type="submit">Tambah Data</button>
              </form>
              <button onClick={this.handleHapusSemua}>Hapus Semua Data</button>
              <TabelData data={this.state.data} />
              <div className="image-container">
                <img src={this.props.imageUrl} alt="Placeholder" />
              </div>
            </div>
          );
        }
      }
      
      App.defaultProps = {
        defaultData: [
          { nama: 'John', umur: 25 },
          { nama: 'Doe', umur: 30 },
          { nama: 'Jane', umur: 28 }
        ],
        imageUrl: 'https://via.placeholder.com/150'
      };
      
    

export default Eventreact;
