import React, { useState } from 'react';

// Komponen Bio menggunakan props untuk menerima data dan state untuk mengatur pesan.
function Bio(props) {
  const [message, setMessage] = useState('You can find my thoughts here.');

  // Fungsi untuk mengubah pesan saat tombol ditekan.
  const changeMessage = () => {
    setMessage('This is the updated message!');
  };

  return (
    <div className="intro">
      <h1>Welcome to my website, {props.name}!</h1>
      <p className="summary">
        {message}
        <br /><br />
        <b>And <i>pictures</i></b> of scientists!
      </p>
      {/* Tombol untuk mengubah pesan */}
      <button onClick={changeMessage}>Change Message</button>
    </div>
  );
}

// Komponen utama yang menggunakan Bio dengan props name.
function App() {
  return (
    <div>
      <Bio name="John" />
    </div>
  );
}

export default App;
