import React, { Component } from 'react';
import './App.css';

class TabelData extends Component {
  render() {
    const { data } = this.props;
    const rows = data.map((item, index) => (
      <tr key={index}>
        <td>{item.nama}</td>
        <td>{item.umur}</td>
      </tr>
    ));

    return (
      <table className="table">
        <thead>
          <tr>
            <th>Nama</th>
            <th>Umur</th>
          </tr>
        </thead>
        <tbody>
          {rows}
        </tbody>
      </table>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: this.props.defaultData,
      nama: '',
      umur: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.clearData = this.clearData.bind(this); // Menambahkan binding untuk fungsi clearData
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit(event) {
    event.preventDefault();
    const { nama, umur } = this.state;
    if (nama && umur && !isNaN(parseInt(umur))) {
      const newData = {
        nama: nama,
        umur: parseInt(umur)
      };
      this.setState(prevState => ({
        data: [...prevState.data, newData],
        nama: '',
        umur: ''
      }));
    } else {
      alert("Input tidak valid. Pastikan Anda memasukkan nama dan umur yang valid.");
    }
  }

  clearData() {
    this.setState({ data: [] }); // Menghapus semua data
  }

  render() {
    return (
      <div className="container">
        <h1>Data Pengguna</h1>
        <form onSubmit={this.handleSubmit}>
          <label>
            Nama:
            <input type="text" name="nama" value={this.state.nama} onChange={this.handleChange} />
          </label>
          <label>
            Umur:
            <input type="text" name="umur" value={this.state.umur} onChange={this.handleChange} />
          </label>
          <button type="submit">Tambah Data</button>
          <button type="button" onClick={this.clearData}>Hapus Semua Data</button> {/* Tombol untuk menghapus semua data */}
        </form>
        <TabelData data={this.state.data} />
      </div>
    );
  }
}

App.defaultProps = {
  defaultData: [
    { nama: 'John', umur: 25 },
    { nama: 'Doe', umur: 30 },
    { nama: 'Jane', umur: 28 }
  ]
};

export default App;
